let turnPly = 0;
function Square(){
	const [value, setValue] = useState(null);
	function handleClick(){
    console.log("value : ", turnPly);
    if(turnPly % 2 === 0)	
      setValue('X');
    if(turnPly % 2 > 0)
      setValue('O');
    turnPly++;
	}
	return(
		<button 
			className="square"
			onClick={handleClick}>
			{value}	
		</button>);
}
